import React from 'react'
import Flashcard from "./Flashcard_7";
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import {Samples} from './Tropes3';
import Switch from '@material-ui/core/Switch';
import open from "./sounds/ui_lock.wav";
import close from "./sounds/ui_unlock.wav";

// import Slider from "@material-ui/core/Slider";
// import VolumeDown from '@material-ui/icons/VolumeDown';
// import VolumeUp from '@material-ui/icons/VolumeUp';

import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';
import Input from '@material-ui/core/Input';
import VolumeUp from '@material-ui/icons/VolumeUp';


const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      // flexDirection: 'reverse',
      // flexWrap: 'reverse'
      // flexDirection: 'column-reverse'
    },
    grid: {
      width: 500,
      height: 450,
      direction: "row",
      justify: "center",
      alignItems: "center",

    },
  }));

//   function prePopulateItems(tropes){
//     let items = new Map();
//     tropes.forEach(element => {
//       items.set(element.id, element);
//     });
//     return items;
//   }

  // let songList = new Map();
  // Samples.forEach(element => {
  //   songList.set(element.id, element);
  // });
//   let songList = prePopulateItems(Samples);
  let songList = Samples;

// export default function FlashcardList( { flashcards, callBack1} ) {
export default function FlashcardList( { callBack1} ) {

  // let songList = [...flashcards];
  // let songList = cloneDeep(flashcards);
  // let songList = new Map();

  const [flip, setFlip] = React.useState(false);
  const [volume, setVolume] = React.useState(.5);

  const handleVolumeChange = (event, newValue) => {
    setVolume(newValue);
    audio.volume = newValue;

    console.log("vol in list is ", volume);
    // document.getElementById(id).volume = volume;
};
const handleInputChange = (event, newValue) => {
    setVolume(newValue === '' ? '' : Number(newValue));
  };

  const handleBlur = () => {
    if (volume < 0) {
      setVolume(0);
    } else if (volume > 1) {
      setVolume(1);
    }
  };

  var audio = new Audio();

  const playSound = (song) => {
        // new Audio(song).play();
        audio.src = song;
        audio.preload = "metadata";
        audio.playbackRate = 2;
        audio.volume = volume;
        audio.controls = true;
        audio.play();

    };

const callBack3 = (val) => {
    console.log("val", val);
   playSound(val);
}

//   const openAudio = new Audio(open);
//   const closeAudio = new Audio(close);

//   const playSound = audioFile => {
//     audioFile.play();
//   };


  const handleChange = (event) => {

    if (event.target.flip) {
      playSound(close);  
    } else {
      playSound(open);
    }

    setFlip(!flip);
    // setState({ ...state, [event.target.name]: event.target.checked });
    var arrayLength = songList.length;
    for (var i = 0; i < arrayLength; i++) {
        songList[i].checked ^= true;
    }
    callBack1(songList);
    // render(FlashcardList(callBack1));
  };



  const callBack2 = (val) => {

    songList[val-1].checked ^= true;

    callBack1(songList);

    // var arrayLength = songList.length;
    // for (var i = 0; i < arrayLength; i++) {
    //     console.log(songList[i]);

        // if (songList[i].id = val){
        //     songList[i].checked = !songList[i].checked;
        // }
        //Do something
    // }
    // songList.forEach(trope => {

    //     if (trope.id = val){
    //         trope.checked = !trope.checked;
    //     }
    // });

    // console.log("i'm FlashcardList callback", songList);
  };



    const classes = useStyles();


    return (


    <div>


         <div className={classes.root}>
         <Grid container direction='row' spacing = {3} alignItems="center" justify="center"> 
            <Grid item  >
                <Switch
                checked={flip}
                onChange={handleChange}
                name="toggle"
                inputProps={{ 'aria-label': 'secondary checkbox' }}
            />
            </Grid>
            </Grid>

            {/* <Grid container spacing={2} alignItems="center"> */}
                {/* <Grid item> */}
                <VolumeUp />
                {/* </Grid> */}
                {/* <Grid item xs> */}
                {/* <Slider id="demo"
                            volume={volume} 
                            onChange={handleVolumeChange} 
                            defaultValue={.5}
                            step={.1} min={0} max={1} 
                            valueLabelDisplay="auto" /> */}
                {/* </Grid> */}
                {/* <Grid item> */}
                <Input
                    volume={volume}
                    width='43'
                    onChange={handleInputChange}
                    onBlur={handleBlur}
                    inputProps={{
                    step: 1,
                    min: 0,
                    max: 100,
                    type: 'number',
                    'aria-labelledby': 'input-slider',
                    }}
                />

                {/* </Grid> */}
            {/* </Grid> */}



                    <br/>
                    <br/>
                    <br/>


                        {/* <Grid item>
                        <VolumeDown />
                        </Grid>
                        <Grid item xs={2}>
                        <Slider id="demo"
                            volume={volume} 
                            onChange={handleVolumeChange} 
                            defaultValue={.5}
                            step={.1} min={0} max={1} 
                            valueLabelDisplay="auto" />
                        </Grid>
                        <Grid item>
                        <VolumeUp />
                        </Grid> */}
        {/* </Grid> */}

      {/* style= { !state.toggle ? { 'font-family': 'Times New Roman'} : { }} > */}
      <Grid container spacing={3} direction='row-reverse' > 
      {songList.map(flash => {
                  return (
                  <Grid item xs={3} >

                    <Flashcard flashcard = {flash} key = {flash.id} callBack2={callBack2} callBack3={callBack3} flip={flip}> </Flashcard>
                    {/* <Flashcard flashcard = {flashcard} key = {flashcard.id}> </Flashcard> */}
                  </Grid>
                  )
        })}

      </Grid>

    </div>
    </div>


    )
}
