import React from 'react';
// import Avatar from '@material-ui/core/Avatar';
// import Button from '@material-ui/core/Button';
// import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
// import FormControlLabel from '@material-ui/core/FormControlLabel';
// import Checkbox from '@material-ui/core/Checkbox';
// import Link from '@material-ui/core/Link';
// import Grid from '@material-ui/core/Grid';
// import Box from '@material-ui/core/Box';
// import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { createMuiTheme } from '@material-ui/core/styles';

import { ThemeProvider } from '@material-ui/styles';



const theme = createMuiTheme({
  palette: {
    primary: {
      light: '#8eacbb',
      main: '#607d8b',
      dark: '#34515e',
      contrastText: '#fff',
    },
    secondary: {
      light: '#718792',
      main: '#455a64',
      dark: '#1c313a',
      contrastText: '#000',
    },
  },
});

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: inherit,
  },
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: 'brown',
    // position: 'center'
  },
  avatar: {
    // margin: theme.spacing(5),
    margin: theme.spacing(3, 0, 2),

    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function Login({handleResult, pwd}) {
  const classes = useStyles();


  function detectDevice(){
    let detectObj = {
      device: !!navigator.maxTouchPoints ? 'mobile' : 'computer',
      orientation: !navigator.maxTouchPoints ? 'desktop' : !window.screen.orientation.angle ? 'portrait' : 'landscape',
      browser: navigator.vendor.includes('Apple')? 'safari' : 'chrome',
    }
    return detectObj
  }

  const headers = new Headers()
  headers.append("Content-Type", "application/json")

  const body = {"message": detectDevice().device + " , " +  detectDevice().orientation + " , " +  detectDevice().browser + ", " + new Date() }

  const options = {
  method: "POST",
  headers,
  mode: "cors",
  body: JSON.stringify(body),
  }

  const fetchData = async () => {
      try {
          const resp = await fetch("https://76103417c60b0ff306268dcb81ecf967.m.pipedream.net", options);
          // const resp = await fetch("https://hooks.zapier.com/hooks/catch/8684953/ogsgyo3", options);

          // alert(resp);
          console.log(resp);
      } 
      catch(err) {
          // alert("err");
          // console.log(resp);
          console.log('failed webhook')

      }
  };

  const handleChange = (event) => {
    if (event.target.value.toLowerCase() === pwd){
        handleResult(true);
        fetchData();
    }
  };

  // <Button  onClick={() => { fetchData()}} > Br </Button> 


  return (
    // <>
    <ThemeProvider theme={theme}>

    <div className={classes.root}>
        <div style={{'color': 'white'}}>
        <h1 >Welcome to Harrison's Bar Mitzvah Website! </h1>
        <br/> </div>

    <Container component="main" max-width="xs" style={{backgroundColor: "brown", position: 'center'}}>
      <div className={classes.paper}>
        {/* <Avatar className={classes.avatar}> */}
          {/* <LockOutlinedIcon /> */}
          <img src="./images/barmit.png" alt="icon" loading="lazy" height='auto'/>

        {/* </Avatar> */}
        {/* <img src="./images/ShemaPic.png" alt="Shema" loading="lazy"  height='auto'/> */}
        <Typography component="h1" variant="h5" style={{color: "white"}}>
          Begin your Journey 
        </Typography>
          <TextField
            variant="outlined"
            color="secondary"
            margin="normal"
            fullWidth
            name="password" label="Password"
            type="password"
            // id="password"
            onChange={handleChange}
            style={{color: "white"}}
            autoComplete="current-password"
          />
      </div>
       </Container>


      </div>
      <h6 style={{backgroundColor: 'green', color: 'black',position: 'flex' }}> Copyright Â© 2020 Year13 Productions. All Rights Reserved.
      Built by Effie Landau. No recordings or lectures may be shared without my written consent.</h6>
      </ThemeProvider>

  );
}
