               <div class="card" onClick={() => {handleChange(1); setValue(1)}} >
                     {/* About Me */}
                   <a class="first-link">
                   {(value !== 1) ?
                   <Link to="/AboutMe">
                        About Me
               {/* <img src="./images/barmit.png" alt="icon" loading="lazy" width="504" height='auto'/> */}
                   </Link>
                      : ''}
                    <Route path="/AboutMe" render={() =>  <AboutMe/>} />
