//TODO FIX UP FLAG STATE FOR PAUSING
import React, {useState, useRef} from 'react'
import Button from "@material-ui/core/Button";
import PauseCircleFilled from '@material-ui/icons/PauseCircleFilled';
import PlayCircleFilled from '@material-ui/icons/PlayCircleFilled';
import {Samples} from './Tropes';

// import SpeedIcon from '@material-ui/icons/Speed';
// import DoubleArrowIcon from '@material-ui/icons/DoubleArrow';

import CachedIcon from '@material-ui/icons/Cached';
import Slider from '@material-ui/core/Slider';

//   function getRandomInt(min, max) {
//     min = Math.ceil(min);
//     max = Math.floor(max);
//     return Math.floor(Math.random() * (max - min)) + min; 
//   }

  function shuffleTropes(items, num){ //https://stackoverflow.com/questions/18806210/generating-non-repeating-random-numbers-in-js
    let nums = [];
    items.forEach(element => {
        if (element.checked){
            nums.push(element);
            }
        });

    let ranNums = [];
    let i = nums.length;
    let j = 0;

    while (i--) {
        j = Math.floor(Math.random() * (i+1));
        ranNums.push(nums[j]);
        nums.splice(j,1);
    }
    return ranNums.slice(0,num);

  }


  function createQuiz(items, num) {
    let order = shuffleTropes(items, num);
    return order;
  }

  let audio = new Audio();

export default function QuizShapes( { ...props} ) {

    // let audio = new Audio();
    let oldTropes = props.flashcards;
    let items = [];
    var arrayLength = oldTropes.length;
    for (var x = 0; x < arrayLength; x++) {
        if (oldTropes[x].checked === 1){
            items.push(oldTropes[x]);
        }
    }
    if (oldTropes.length === undefined){
        items = Samples;
    }

    // let fast = 1;
    // const speeds = [.5, 1, 1.5, 2, 2.5];
    const [flag, _setFlag] = useState(false);
    const [trope, _setTrope] = useState(0);

    // const [myState, _setMyState] = React.useState(0);

    const tropeRef = React.useRef(trope);
    const setTrope = data => {
        tropeRef.current = data;
        _setTrope(data);
    };

    const flagRef = React.useRef(flag);
    const setFlag = data => {
        flagRef.current = data;
        _setFlag(data);
    };
    // const [speed, setSpeed] = useState(1);

    // const handleSpeed = (val) => {
    //     // setSpeed(speed => speed = val);
    //     setSpeed(val);
    //   }


    // let i = items.length;
    var i = 0;
    var myflag = false;
    // var speed = 1;

    // function getSpeed(){
    //     return speed;
    // }

    function cont (index = 0) {
        // var index = 1;
        // console.log("HEA", index, quiz[index].sound, quiz)
        // audio.src = quiz[index].sound;


        if(myflag && index < quiz.length) {
            setTrope(index);

            audio.src = quiz[index].sound;
            audio.playbackRate = 2;


            var playPromise = audio.play();

            if (playPromise !== undefined) {
                playPromise.then(_ => {
                // Automatic playback started!
                // Show playing UI.
                    // console.log("playing ", next_trope);

                })
                .catch(error => {
                // Auto-play was prevented
                // Show paused UI.
                    // console.log("promise broke in playnext");
                });
            }
            if (index === quiz.length-1){
                resetFlag();
                console.log("last cont")

                // setFlag(false);
                // myflag = false;
            }
        }
        else if(index >= quiz.length){
            console.log("last cont")
        }
    };


    const resetFlag = () => {
        flagRef.current = true;
        _setFlag(true);
        myflag = true;
        setFlag(true);
    };

    function findIndex (src){
        // console.log(quiz)
        var w = -1;
        for (w = 0; w < quiz.length; w++){
            // console.log("w: ", w, "quiz[w].sound ", quiz[w].sound, " soruce is ", src, " ", src.replace(/^.*(\\|\/|\:)/, ''))
            let file = './sounds/' + src.replace(/^.*(\\|\/|\:)/, '')
            if (quiz[w].sound === file){
                // console.log("here")
                return w;
            }
        }
        // return quiz.length-1;

    }

    // audio.addEventListener('ended', cont((findIndex(audio.src)+1)), false);
    audio.addEventListener('ended', function(){
        // cont((findIndex(audio.src)+1));
        // cont((findIndex(audio.src)+1));
        i = i + 1;
        if (i < quiz.length){
            setFlag(true); 
            cont(i);
        } 
        else{
            // setFlag(false);
            // myflag = false;
            audio.pause();
            i = 0;
            setTrope(0);

            resetFlag()
            console.log("last end")
            // // flag = false;
            // _setFlag(false);
            // setFlag(false);
            // // audio.pause();

            // myflag = false;
        }

    }, false);


    const [quiz, _reloadQuiz] = React.useState(createQuiz(items, Math.min(Math.floor(items.length/2),8)));
    const [value, setValue] = React.useState(4);
    const quizRef = React.useRef(quiz);

    const reloadQuiz = data => {
        quizRef.current = data;
        _reloadQuiz(data);
    };

    const handleChange = (event, newValue) => {
        setValue(newValue);
        reloadQuiz(createQuiz(items, newValue))
    };

    // playFromSong
    const playFromSong = (index) => {

        myflag = true;
        setFlag(true);
        // enableHighlight(true);
        i = index;
        setTrope(index);
        cont(i);

    };

    function handlePlay(){
        // myflag = 0;
        // setTrope(quiz.length);
        // i = quiz.length;
        // setTrope(0);

        // printc("handlePlay b4 PlayNext");
        // playNext();
        i = trope;
        myflag = true;
        setFlag(true); 
        // playNext(i);
        // continuous(0);
        cont(i);
        // makeSongList(quiz);
    }

    function handlePause(){
        // myflag = 1;
        // // audio.pause();
        // audio.pause()
        // audio.currentTime = 0
        // audio = null;
        // setTrope(i);
        myflag = false;
        setFlag(false);
        audio.pause();

        // killAudio();
        // i = 0;
        // setTrope(0);



    }

    // function handlePlay(){
    //     myflag = 0;
    //     // setTrope(quiz.length);
    //     // i = quiz.length;
    //     setTrope(0);

    //     // printc("handlePlay b4 PlayNext");
        // playNext();
    //     setFlag(true); 

    //     // makeSongList(quiz);
    // }

    // function handlePause(){
    //     myflag = 1;
    //     // audio.pause();
    //     audio.pause()
    //     audio.currentTime = 0
    //     audio = null;

    //     // printc("handlePause after setFlag");
    //     setFlag(false);
    //     i = 0;
    //     setTrope(0);



    // }
    // const enableHighlight = (e) => {
    //                 console.log("do");

        // audio.addEventListener('timeupdate', function () {
            // if (!myflag) {
            //     playNext();
            //     }
            // else {
            //     audio.pause();
            // }
            // console.log(audio.currentTime.toFixed(1));

        // }, false);

    // }

    function killAudio(){
        audio.pause();
        // audio = null;
    }


    return (

         <quiz>Quiz Yourself!
            <h5> 

            <div className="controls" >

                <Button variant="contained" color="primary"
                    endIcon={<CachedIcon  onClick={() =>  {reloadQuiz(createQuiz(items, value));}}/>}>
                </Button> 

                {/* <Button variant="contained" color="primary"
                    startIcon = {<DoubleArrowIcon onChange={() => { handleSpeed(2) ;}} />}>
                        {speed}
                </Button> 
 */}

                <Button size="small" variant="contained"> 
                    <Slider value={value} onChange={handleChange} defaultValue={Math.min(Math.floor((items.length+1)/2),4)}
                            step={1}  min={0} max={Math.min(items.length, 8)} valueLabelDisplay="auto" />
                            </Button>



                {/* only shows play or pause based off flag state.. flag doesn't work must be fixed! */}
                {!flag ?  <Button variant="contained" color="primary" 
                    endIcon={<PlayCircleFilled onClick={() => handlePlay()}/>}>
                    </Button> :
                    <Button variant="contained" color="primary" 
                        endIcon={<PauseCircleFilled onClick={() => handlePause()}/>}>
                    </Button>
                }
            </div>

            </h5>

            <p>
        {quiz.map((songName, index)=> {return ( 
            (trope === index) ?

            // <img src={songName.image} onClick={() => playFromSong(index-1)} alt="icon" loading="lazy" width="50" height="50" style={ {'opacity' : '.5'} }/>
            // :<img src={songName.image} onClick={() => playFromSong(index-1)} alt="icon" loading="lazy" width="50" height="50" style={  {'border' : '0px solid black'}  }/>
            <img src={songName.image} onClick={() => playFromSong(index)} alt="icon" loading="lazy" width="50" height="50" style={ {'opacity' : '.5'} }/>
            :<img src={songName.image} onClick={() => playFromSong(index)} alt="icon" loading="lazy" width="50" height="50" style={  {'border' : '0px solid black'}  }/>
            // <img src={songName.image} onClick={() => cont(index)} alt="icon" loading="lazy" width="50" height="50" style={ {'opacity' : '.5'} }/>
            // :<img src={songName.image} onClick={() => cont(index)} alt="icon" loading="lazy" width="50" height="50" style={  {'border' : '0px solid black'}  }/>

           )})}
        </p>

           </quiz>

    )
}
