import React, {useState} from 'react'
// import {Shemas} from './Effie';
// const hebrewLetters = "אבגדהוזחטיכךלמםנןסעפףצץקרשת" ;

// const hebrewLetters = "אבבּגדהוזחטיככּךלמםנןסעפפּףצץקרשׁשׂתתּ" ;
const nekudot = " ֯ ֮ ֭ ֬ ֫ ֩ ֨ ֧ ֦ ֥ ֤ ֣ ֽ֢ ֢ ֡ ֠ ֟ ֞ ֝ ֜ ֛ ֚  ֙ ֘ ֗ ֖ ֕  ֔ ֓ ֒ ֑  ׀    ֑♢ "

const Letter = ({l}) => {

    const [flip, setFlip] = useState(false);

    // return ( 

    if (nekudot.indexOf(String(l)) === -1) {

        return (<hov style={flip? {'color': 'red'} : '' } onDoubleClick={() => {setFlip(!flip)}} >{l[0]}</hov>)}

        // return ( <hov  onClick={() => {setFlip(!flip)}} >{l}</hov>)}

        // <Letter l={l[0]}></Letter>
        //   return ( <hov style={{'color': flip && l[1]}} onClick={() => {setFlip(!flip)}} >{l[0]}</hov>)}

    else {
        return (<hov style={flip? {'color': 'red'} : '' } onDoubleClick={() => {setFlip(!flip)}} ><vowel>&shy;{l[0]}</vowel></hov>)
    }

    // <hov style={{'color': flip && 'red'}} onClick={() => {setFlip(!flip)}} contenteditable="true">
    //     {l[0]}
    // </hov>

  };


export default function Sophie ( {p}) {
    // &nbsp;
    // const [flip, setFlip] = useState(false);

    var b = p.map(s => { 
        return ( 
          <>{s.map(w => {
              return (
                  <>
                  <hov2>
                      {w.map(l => {return ( 
                        //   if (nekudot.indexOf(String(l)) === -1) {
                        //     return ( <hov  onClick={() => {setFlip(!flip)}} >{l}</hov>)}
                        //     // <Letter l={l[0]}></Letter>
                        //     //   return ( <hov style={{'color': flip && l[1]}} onClick={() => {setFlip(!flip)}} contenteditable="true">{l[0]}</hov>)}

                        //   else {
                        //       return (<hov style={{'color': flip && l[1]}} onClick={() => {setFlip(!flip)}} contenteditable="true"><vowel>&shy;{l}</vowel></hov>)}
                          <Letter l={l}></Letter>
                          )} 
                           ) }
                  </hov2> 
                  &nbsp;
                  </>
              ) })} 
              <br/> 
          </> 
        )
      })

    return (
        <boxy>{b}</boxy>
);
}

export function Batsheva ( {p}) {

    // const [flip, setFlip] = useState(false);

    // &nbsp;
    return (
        <boxy>
        <span>
            {p.map(s => {
                  return (
                    <>{s.map(w => {
                        return (
                            <>
                            {/* &#x5c3;&#x5c3;&#1475;&#1425; */}
                            <hov2>
                                {w.map(l => {return ( 
                                                              <Letter l={l}></Letter>

                                // <hov onClick={() => {setFlip(!flip)}} contenteditable="true" >{l}</hov>
                                )} 
                                )}
                            </hov2> 
                            &nbsp;
                            </>
                        ) })} 
                        <br/> 
                    </> 
                  )
                })}
        </span>
        </boxy>
);
}
