import React, {useState} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import ReactColorPicker from '@super-effective/react-color-picker'; //https://github.com/super-effective/react-color-picker
import Tooltip from '@material-ui/core/Tooltip';

function getModalStyle() {
  const top = 150;
  const left = 150;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({

  paper: {
    position: 'absolute',
    backgroundColor: 'rgb(204, 204, 204)',
    top: '350',
    left: '350',
    transform: `translate(-50%, -50%)`,
    border: '5px solid #e7e7e7',
    padding: '5px 32px',
    margin: '5px auto'
  },
}));

export default function ColorModal({callBack}) {

  const classes = useStyles();
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [color, setColor] = useState('white');

  const onColorChange = (updatedColor) => {
    setColor(updatedColor);
    callBack(updatedColor);
  }

  return (
    <>
      <Tooltip title="Adjust Text Color" arrow placement="right">
        <button type="button" onClick={handleOpen}>
          Color
        </button>
      </Tooltip> 
        <Modal open={open} onClose={handleClose} style = {{
                                              "width" : "200px",
                                              "height" : "200px",
                                              // "padding" : "15px 5px",
                                              // "border" : "1px solid #ccc",
                                              // "border-radius" : "10px",
                                              // // "position": 'flex',
                                              "backgroundColor": 'rgb(244, 155, 137)',
                                              "left" : "50%",
                                              "top" : "50%",
                                              "transform": "translate(-50%, -50%)",

                                              // "transform": "translate(150%, 150%)"
                                              }}>
          {/* <div style={modalStyle} className={classes.paper}> */}

                  <ReactColorPicker style = {{
                                              "width" : "200px",
                                              "height" : "200px",
                                              "padding" : "15px 5px",
                                              "border" : "1px solid #ccc",
                                              "border-radius" : "10px",
                                              // // "position": 'flex',
                                              "backgroundColor": 'rgb(151, 155, 137)',
                                              // "transform": "translate(150%, 150%)"
                                              }}
                      showHex = {false} showSwatch = {false}
                      color={color} onChange={onColorChange} 
                  />
             {/* </div> */}

        </Modal>
    </>
  );
}
