        if (element.checked){
            nums.push(element);
            }
        });

    let ranNums = [];
    let i = nums.length;
    let j = 0;

    while (i--) {
        j = Math.floor(Math.random() * (i+1));
        ranNums.push(nums[j]);
        nums.splice(j,1);
    }
    return ranNums.slice(0,num);

  }


  function createQuiz(items, num) {
    let order = shuffleTropes(items, num);
    return order;
  }
