// import React from 'react';

function par_to_letter_list(paragraph){
    var sentence; var word; var letter;
    var w = []; var s = []; var p = [];
    for (sentence of paragraph.split("\n")){
        for (word of sentence.split(" ")){
            for (letter of word){ 
                w.push(letter);
            }
            s.push(w); w = [];
        }
        p.push(s); s = [];
    }
    return p;
}




