import React, {useState} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import ReactColorPicker from '@super-effective/react-color-picker'; //https://github.com/super-effective/react-color-picker

function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    display: 'center',
    backgroundColor: 'rgb(204, 204, 204)',
    border: '5px solid #e7e7e7',
    padding: '5px 32px',
    margin: '5px auto'
  },
}));

export default function ColorModal({callBack}) {

    const classes = useStyles();
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [color, setColor] = useState('white');

  const onColorChange = (updatedColor) => {
    setColor(updatedColor);
    callBack(color);
  }

  return (
    <div>
      <button type="button" onClick={handleOpen}>
        bru
      </button>
      <Modal open={open} onClose={handleClose}>
        <div className={classes.paper}>
            <p id="simple-modal-description">
            <div>
                <ReactColorPicker style = {{"width" : "200px",
                                            "height" : "200px",
                                            "padding" : "15px 5px",
                                            "border" : "1px solid #ccc",
                                            "border-radius" : "10px",
                                            }}
                    showHex = {false} showSwatch = {false}
                    color={color} onChange={onColorChange} 
                />
            </div>
            </p>
        </div>
      </Modal>
    </div>
  );
}
