let sorted = [...Slideshows].sort((a, b) => (new Date(b.date)) - (new Date(a.date)));

export default function Slideshow(){

  const classes = useStyles();
  const [sortType, setSortType] = useState('date');
  const [data, setData] = useState(sorted);
  const [asc, setAsc] = useState(1);

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = (type) => {
    setAnchorEl(null);
  };


  const handleChange = (type) => {

    if (sortType === type){
      setAsc(-1*asc);
    }
    else{
      setAsc(1);
    }

    switch (type) {
      case 'asc':
        console.log('asc type: ', sortType);
        setAsc(-1*asc);
        handleChange(sortType);
        return;
      case 'desc':
        // console.log('asc type: ', sortType);
        setAsc(1);
        handleChange(sortType);
        return;
      case 'id':
        sorted = [...Slideshows].sort((a, b) =>  (asc * b.id) -  (asc * a.id));
        break;
      case 'date':
        sorted = [...Slideshows].sort((a, b) => (asc * (new Date(b.date))) - (asc * (new Date(a.date))));
        break;
      case 'name':
          sorted = [...Slideshows].sort((a, b) => ((b.name.toLowerCase < a.name.toLowerCase) ? asc * -1 : asc * 1));
          break;
      case 'size':
        sorted = [...Slideshows].sort((a, b) => (asc * b.images_path.length) - (asc * a.images_path.length));
        break;
    }
    setSortType(type);
    setData(sorted);
    handleClose();

  };

  return (

    <div className={classes.root}>
        <div style={{color: 'white' }}> 
              <h1>Review the Lectures! 

                <Button variant="contained" color="primary" onClick={handleClick}>
                Sort By: {sortType}
              </Button></h1>

        </div> 
      <div>
      <Grid item xs={12} sm container>

  {/* <Grid container spacing={2} justify="space-between" alignItems="center">  */}
      <Grid item>
        <Menu id="simple-menu" anchorEl={anchorEl} keepMounted open={Boolean(anchorEl)} onClose={handleClose}>
          <MenuItem onClick={() => handleChange('date')}>
            Date 
            {(sortType === 'date') ?
              (asc!==1) ? <ExpandLessIcon onClick={() => handleChange('asc')}/> : <ExpandMoreIcon onClick={() => handleChange('desc')}/>
              :
              ''
            }
              </MenuItem>
          <MenuItem onClick={() => handleChange('name')}>
            Name 
            {(sortType === 'name') ?
              (asc!==1) ? <ExpandLessIcon onClick={() => handleChange('asc')}/> : <ExpandMoreIcon onClick={() => handleChange('desc')}/>
              :
              ''
            }
          </MenuItem>
          <MenuItem onClick={() => handleChange('size')}>
            Size 
              {(sortType === 'size') ?
                (asc!==1) ? <ExpandLessIcon onClick={() => handleChange('asc')}/> : <ExpandMoreIcon onClick={() => handleChange('desc')}/>
                :
                ''
              }
          </MenuItem>
          {/* <MenuItem onClick={() => handleChange('id')}>Id</MenuItem> */}
        </Menu>
      </Grid>
      <Grid item>
        <Tooltip title="Sort Direction" arrow placement="down">

        {(asc!==1) ? 
            <Button style={{maxWidth: '30px', maxHeight: '30px', minWidth: '30px', minHeight: '30px'}} variant="contained" color="primary" 
            onClick={() => handleChange('asc')}><ExpandLessIcon onClick={() => handleChange('asc')}/>
            </Button> :
            <Button style={{maxWidth: '30px', maxHeight: '30px', minWidth: '30px', minHeight: '30px'}} variant="contained" color="primary" 
            onClick={() => handleChange('desc')}><ExpandMoreIcon onClick={() => handleChange('desc')}/>
            </Button>
        }
        </Tooltip>
      </Grid>
      </Grid>


      <Grid container spacing={4} justify="space-between" alignItems="center"> 
        {data.map(lesson => { return (
              <Grid item sm={4} xs={6}>
                <Yalla lesson = {lesson}/>
              </Grid>
          )})}
      </Grid>

      </div>

    </div>

  );
}
