import React from 'react'
import Flashcard from "./Flashcard_8";
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';

import Grid from '@material-ui/core/Grid';
import {Samples} from './Tropes3';
import Switch from '@material-ui/core/Switch';
import open from "./sounds/ui_lock.wav";
import close from "./sounds/ui_unlock.wav";

import Slider from "@material-ui/core/Slider";
import VolumeDown from '@material-ui/icons/VolumeDown';
import VolumeUp from '@material-ui/icons/VolumeUp';


const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      color: 'green',
    },
    grid: {
      width: 500,
      height: 450,
      direction: "row",
      justify: "center",
      alignItems: "center",

    },
    paper: {
    padding: theme.spacing(1),
    border: '8px double',
    color: 'white',
    backgroundColor: 'brown',
    },
  }));



  let songList = Samples;

export default function AllTropesBox( {} ) {


  const [flip, setFlip] = React.useState(false);
  const [volume, setVolume] = React.useState(.5);


    const classes = useStyles();

    const playSound = (song) => {
        // new Audio(song).play();
        console.log(volume);
        var audio = new Audio();
        audio.src = song;
        audio.preload = "auto";
        audio.playbackRate = 2;
        audio.volume = volume;
        audio.controls = true;
        audio.play();

      };
    const handleVolumeChange = (event, newValue) => {
        setVolume(newValue);
        // document.getElementById(id).volume = volume;
    };

    const handleChange = (event) => {
        setFlip(!flip);

        if (event.target.flip) {
            playSound(close);  
        } else {
            playSound(open);
        }
    };


    return (
<div>

         <div className={classes.root}>
            <Paper className={classes.paper} >

            <Grid container direction='column' spacing={2} > 
                <Grid item xs={2}>
                    <Switch
                    checked={flip}
                    onChange={handleChange}
                    name="toggle"
                    inputProps={{ 'aria-label': 'secondary checkbox' }}
                    />
                     <Grid container spacing={12}>
                        <Grid item>
                        <VolumeDown />
                        </Grid>
                        <Grid item xs={4}>
                        <Slider id="demo"
                            volume={volume} 
                            onChange={handleVolumeChange} 
                            defaultValue={.5}
                            step={.1} min={0} max={1} 
                            valueLabelDisplay="auto" />
                        </Grid>
                        <Grid item>
                        <VolumeUp />
                        </Grid>
                    </Grid>

                </Grid>

            <Grid item xs={9}>

            <Grid container direction='row-reverse'  > 

        {/* style= { !state.toggle ? { 'font-family': 'Times New Roman'} : { }} > */}

                {songList.map(flash => {
                            return (
                                <fl>
                            <Grid item id='c' onClick = {() => {playSound(flash.sound)}}>
                            {/* { !state.toggle ?  */}
                                {/* // {flash.heb} {'\u00A0'} : {flash.heb} {'\u00A0'}{flash.heb} {'\u00A0'} */}
                                 {flash.eng} {'\u00A0'}
                            </Grid>
                            </fl>
                            )
                    })}

                </Grid>
        </Grid>
        </Grid>


        </Paper>

        </div>
        </div>

    )
}
