import React, {useState} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import ButtonBase from '@material-ui/core/ButtonBase';
import Checkbox from '@material-ui/core/Checkbox';
import CheckBoxOutlineBlankIcon from '@material-ui/icons/CheckBoxOutlineBlank';
import CheckBoxIcon from '@material-ui/icons/CheckBox';
import Tooltip from '@material-ui/core/Tooltip';
// import IconButton from "@material-ui/core/IconButton";
import Hidden from '@material-ui/core/Hidden';

// import Slider from "@material-ui/core/Slider";
import CircularProgress from '@material-ui/core/CircularProgress';
// import EqualizerIcon from '@material-ui/icons/Equalizer';
// import LanguageTwoToneIcon from '@material-ui/icons/LanguageTwoTone';

import Button from '@material-ui/core/Button';
import Snackbar from '@material-ui/core/Snackbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';

// import Snackbar from '@material-ui/core/Snackbar';
// import MuiAlert from '@material-ui/core/Alert';

// function Alert(props) {
//   return <MuiAlert elevation={6} variant="filled" {...props} />;
// }


const useStyles = makeStyles((theme) => ({
  root: {
    // flexGrow: 1,
    // backgroundColor: 'green',
    color: 'white',

    // display: 'flex',
    // flexWrap: 'wrap',
    // justifyContent: 'space-around',
    // overflow: 'hidden',

  },
  paper: {
    // padding: theme.spacing(2),
    // padding: '5% 10%',
    padding: '5%',
    // margin: 'auto',
    border: '4px double',
    color: 'white',
    // maxHeight: '%50',
    backgroundColor: 'brown',


    // display: 'block',
    // width: '10vw',
    // transitionDuration: '0.3s',
    // height: '8vw'

    //     display: 'flex',
    // flexWrap: 'wrap',
    // justifyContent: 'space-around',
    // overflow: 'hidden',
  },
  image: {
    // width: 100,
    height: 100,
  },
  img: {
    margin: 'auto',
    // display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
  progress: {
    position: 'relative',

    top: 30,
    // top: -50,
    left: 80,
    // left: 54,
    // zIndex: 2,
  },
  translation: {
    top: -1,
    right:0,
    // zIndex: 2,
  },
  close: {
    padding: theme.spacing(0.5),
  },
}));

export default function Flashcard( {...props}) {
    const classes = useStyles();

    const [snackPack, setSnackPack] = React.useState([]);
    const [open, setOpen] = React.useState(false);
    const [messageInfo, setMessageInfo] = React.useState(undefined);

    React.useEffect(() => {
      if (snackPack.length && !messageInfo) {
        // Set a new snack when we don't have an active one
        setMessageInfo({ ...snackPack[0] });
        setSnackPack((prev) => prev.slice(1));
        setOpen(true);
      } else if (snackPack.length && messageInfo && open) {
        // Close an active snack when a new one is added
        setOpen(false);
      }
    }, [snackPack, messageInfo, open]);

    const handleClick = (message) => () => {
      setSnackPack((prev) => [...prev, { message, key: new Date().getTime() }]);
    };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpen(false);
  };

  const handleExited = () => {
    setMessageInfo(undefined);
  };

    let toggle = props.flashcard.checked;

    const [center, setcEnter] = useState(props.flashcard.checked);

  // const handleChange2 = (event) => {
  //   setChecked(event.target.checked);
  //   var arrayLength = songList.length;
  //   for (var i = 0; i < arrayLength; i++) {
  //       props.flashcard.checked ^= true;
  //   }
  //   callBack1(songList);
  //   // render(FlashcardList(callBack1));
  // };


  const handleChange = (event) => {
    // setChecked(event.target.checked);
    // setEnter(!enter);
    setcEnter(!center);
    toggle ^= true;
    props.callBack2(props.flashcard.id);
    console.log("checked id", props.flashcard.id);
  };

  const [flip, setFlip] = useState(false);
  // const playSound = audioFile => {
  //   audioFile.play();
  // };
  // const song =  new Audio(props.flashcard.sound)

  //TODO: handle volume
  // const [volume, setVolume] = React.useState(.5);

  const [load, setLoading] = React.useState(false);

  const [playing, setPlaying] = React.useState(false);

  var audio = new Audio();

  audio.addEventListener('playing', function () {
    setLoading(false);
    setPlaying(true);
}, false);

  audio.addEventListener('ended', function () {
    setPlaying(false);
  }, false);

  const playSound = (song) => {
      // new Audio(song).play();
      console.log("vol in card is", props.volume);
      setLoading(true);

      audio.src = song;
      audio.preload = "metadata";
      audio.playbackRate = 1;
      audio.volume = props.volume;
      audio.controls = true;
      audio.play();

    };
    //TODO: handle volume
  // const handleVolumeChange = (event, newValue) => {
  //     // setVolume(newValue);
  //     audio.volume = newValue;
  //     // document.getElementById(id).volume = volume;
  // };
  ///

  return (

    <div className={classes.root}>

      {/* <Paper className={classes.paper} onClick={() => {playSound(props.flashcard.sound)}} > */}
      <Paper className={classes.paper} onClick={() => {playSound(props.flashcard.sound)}} >

        <div>
          <Snackbar key={messageInfo ? messageInfo.key : undefined}
            anchorOrigin={{ vertical: 'bottom', horizontal: 'left',}}
            open={open} autoHideDuration={3000}
            onClose={handleClose} onExited={handleExited} message={messageInfo ? messageInfo.message : undefined}
            action={
              <React.Fragment>            
