import React from 'react'
import Flashcard from "./Flashcard_8";
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';

import Grid from '@material-ui/core/Grid';
import {Samples} from './Tropes3';
import Switch from '@material-ui/core/Switch';

const useStyles = makeStyles((theme) => ({
    root: {
    //   display: 'flex',
      flexWrap: 'wrap',
    //   justifyContent: 'space-around',
      overflow: 'hidden',
      color: 'white',
    },
    grid: {
    //   width: 500,
    //   height: 450,
      direction: "row",
      justify: "center",
      alignItems: "center",

    },
    paper: {
    // padding: theme.spacing(2),
    // padding: '5% 10%',
    // padding: '10%',
    // margin: 'auto',
    border: '4px double',
    color: 'white',
    font: 'Comic Sans MS',
    // maxHeight: '%50',
    backgroundColor: 'brown',
    },
  }));



  let songList = Samples;

export default function AllTropesBox( {} ) {


  const [flip, setFlip] = React.useState(false);

  const handleChange = (event) => {
    setFlip(!flip);

  };


    const classes = useStyles();

    const playSound = (song) => {
        // let audioFile =  new Audio(song);
        new Audio(song).play();
      };

    return (


         <div className={classes.root}>
            <Paper className={classes.paper} >

                <Switch
                checked={flip}
                onChange={handleChange}
                name="toggle"
                inputProps={{ 'aria-label': 'secondary checkbox' }}
            />


        {/* style= { !state.toggle ? { 'font-family': 'Times New Roman'} : { }} > */}

        <Grid container direction='row-reverse'  > 
        {songList.map(flash => {
                    return (
                        <fl>
                    <Grid item onClick = {() => {playSound(flash.sound)}}>
                        {flash.heb} ''
                    </Grid>
                    </fl>
                    )
            })}

        </Grid>

        </Paper>

        </div>


    )
}
