       <img src="/Slides3/loadin.gif" alt="icon" width='250%' position="absolute" style={{display: 'none'}}/>

        </frontpage>
        </div>

        <img src={lol[0][0]} alt="joseph" width='25%'/>

            {lol.map(image => {

                 return (

                     <img src={image} alt="joseph" width='25%'/>

                     // <img src="/Slides3/loadin.gif" alt="icon" width='250%' position="absolute" style={{display: 'none'}}/>

                 )
             })}

