import React, {useState} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import ReactColorPicker from '@super-effective/react-color-picker'; //https://github.com/super-effective/react-color-picker

function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    backgroundColor: 'rgb(204, 204, 204)',
    border: '5px solid #e7e7e7',
    padding: '5px 32px',
    margin: '5px auto'
  },
}));

export default function ColorModal({callBack}) {

    // const classes = useStyles();
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [color, setColor] = useState('white');

  const onColorChange = (updatedColor) => {
    setColor(updatedColor);
    callBack(color);
  }

  return (
    <div>
      <button type="button" onClick={handleOpen}>
        bru
      </button>
      <iframe src="https://codesandbox.io/embed/color-picker-forked-ggo7f?autoresize=1&fontsize=14&theme=dark&view=preview"
            style="width:100%; height:500px; border:0; border-radius: 4px; overflow:hidden;"
            title="Color Picker (forked)"
            allow="accelerometer; ambient-light-sensor; camera; encrypted-media; geolocation; gyroscope; hid; microphone; midi; payment; usb; vr; xr-spatial-tracking"
            sandbox="allow-forms allow-modals allow-popups allow-presentation allow-same-origin allow-scripts"
    ></iframe>

    </div>
  );
}
