//TODO FIX UP FLAG STATE FOR PAUSING
import React, {useState, useRef} from 'react'
import Button from "@material-ui/core/Button";
import PauseCircleFilled from '@material-ui/icons/PauseCircleFilled';
import PlayCircleFilled from '@material-ui/icons/PlayCircleFilled';
import {Samples} from './Tropes';

// import SpeedIcon from '@material-ui/icons/Speed';
// import DoubleArrowIcon from '@material-ui/icons/DoubleArrow';

import CachedIcon from '@material-ui/icons/Cached';
import Slider from '@material-ui/core/Slider';

//   function getRandomInt(min, max) {
//     min = Math.ceil(min);
//     max = Math.floor(max);
//     return Math.floor(Math.random() * (max - min)) + min; 
//   }

  function shuffleTropes(items, num){ //https://stackoverflow.com/questions/18806210/generating-non-repeating-random-numbers-in-js
    let nums = [];
    items.forEach(element => {
        if (element.checked){
            nums.push(element);
            }
        });

    let ranNums = [];
    let i = nums.length;
    let j = 0;

    while (i--) {
        j = Math.floor(Math.random() * (i+1));
        ranNums.push(nums[j]);
        nums.splice(j,1);
    }
    return ranNums.slice(0,num);

  }


  function createQuiz(items, num) {
    let order = shuffleTropes(items, num);
    return order;
  }
  let audio = new Audio();

export default function QuizShapes( { ...props} ) {

    // let audio = new Audio();
    let oldTropes = props.flashcards;
    let items = [];
    var arrayLength = oldTropes.length;
    for (var x = 0; x < arrayLength; x++) {
        if (oldTropes[x].checked === 1){
            items.push(oldTropes[x]);
        }
    }
    if (oldTropes.length === undefined){
        items = Samples;
    }

    // let fast = 1;
    // const speeds = [.5, 1, 1.5, 2, 2.5];
    const [flag, _setFlag] = useState(false);
    const [trope, _setTrope] = useState(0);

    // const [myState, _setMyState] = React.useState(0);

    const tropeRef = React.useRef(trope);
    const setTrope = data => {
        tropeRef.current = data;
        _setTrope(data);
    };

    const flagRef = React.useRef(flag);
    const setFlag = data => {
        flagRef.current = data;
        _setFlag(data);
    };
    // const [speed, setSpeed] = useState(1);

    // const handleSpeed = (val) => {
    //     // setSpeed(speed => speed = val);
    //     setSpeed(val);
    //   }


    // let i = items.length;
    let i = 0;
    let myflag = false;
    // var speed = 1;

    // function getSpeed(){
    //     return speed;
    // }

    // const [count, setCount] = useState(0);
    // const [yesQuiz, setQuizTrue] = useState(0);



    function playNext (next_trope){

        audio.src = quiz[next_trope].sound;
        setTrope(next_trope);

        var playPromise = audio.play();

        if (playPromise !== undefined) {
            playPromise.then(_ => {
            // Automatic playback started!
            // Show playing UI.
                // console.log("playing, can't play next");

            })
            .catch(error => {
            // Auto-play was prevented
            // Show paused UI.
                // console.log("paused, can't play next");
            });
        }

    }

    audio.addEventListener('played', function(ev){

        // console.log("ended w i, ", i)
        // ++i;
        i = i + 1;
        setTrope(i);
        tropeRef.current = i;

        // tropeRef.current = (i+1) % quiz.length;

        // if (flagRef) {

        // playNext(tropeRef.current+1);
        // break;
        // }

    }, false);

    audio.addEventListener('ended', function(ev){

        console.log("ended w i, ", i)
        // ++i;
        setTrope(i);
        console.log("calling next w i, ", i)

        // tropeRef.current = (i+1) % quiz.length;

        // if (flagRef) {

        playNext(tropeRef.current+1);
        // }

    }, false);

    const [quiz, reloadQuiz] = useState(createQuiz(items, Math.min(Math.floor(items.length/2),8)));
    const [value, setValue] = React.useState(4);

    const handleChange = (event, newValue) => {
        setValue(newValue);
        reloadQuiz(createQuiz(items, newValue))
    };

    // playFromSong
    const playFromSong = (index) => {

        setFlag(true);
        myflag = true;
        // enableHighlight(true);
        i = index;
        setTrope(index);
        playNext(i);

        // audio.src = "";
        // console.log('index was ', i, 'now is ', index, ' and trope was ', trope);
        // i = index;
        // setTrope(i);
        // console.log('trope now ', trope);
        // playNext();
    };

    function handlePlay(){
        // myflag = 0;
        // setTrope(quiz.length);
        // i = quiz.length;
        // setTrope(0);

        // printc("handlePlay b4 PlayNext");
        // playNext();
        i = trope;
        setFlag(true); 
        myflag = true;
        playNext(i);


        // makeSongList(quiz);
    }

    function handlePause(){
        // myflag = 1;
        // // audio.pause();
        // audio.pause()
        // audio.currentTime = 0
        // audio = null;

        myflag = false;
        setFlag(false);
        audio.pause();

        // killAudio();
        // i = 0;
        // setTrope(0);



    }

    // function handlePlay(){
    //     myflag = 0;
    //     // setTrope(quiz.length);
    //     // i = quiz.length;
    //     setTrope(0);

    //     // printc("handlePlay b4 PlayNext");
        // playNext();
    //     setFlag(true); 

    //     // makeSongList(quiz);
    // }

    // function handlePause(){
    //     myflag = 1;
    //     // audio.pause();
    //     audio.pause()
    //     audio.currentTime = 0
    //     audio = null;

    //     // printc("handlePause after setFlag");
    //     setFlag(false);
    //     i = 0;
    //     setTrope(0);



    // }
    // const enableHighlight = (e) => {
    //                 console.log("do");

        // audio.addEventListener('timeupdate', function () {
            // if (!myflag) {
            //     playNext();
            //     }
            // else {
            //     audio.pause();
            // }
            // console.log(audio.currentTime.toFixed(1));

        // }, false);

    // }

    function killAudio(){
        audio.pause();
        // audio = null;
    }


    return (

         <quiz>Quiz Yourself!
            <h5> 

            <div className="controls" >


                <Button variant="contained" color="primary"
                    endIcon={<CachedIcon  onClick={() =>  {reloadQuiz(createQuiz(items, value));}}/>}>
                </Button> 

                {/* <Button variant="contained" color="primary"
                    startIcon = {<DoubleArrowIcon onChange={() => { handleSpeed(2) ;}} />}>
                        {speed}
                </Button> 
 */}

                <Button size="small" variant="contained"> 
                    <Slider value={value} onChange={handleChange} defaultValue={Math.min(Math.floor((items.length+1)/2),4)}
                            step={1}  min={0} max={Math.min(items.length, 8)} valueLabelDisplay="auto" />
                            </Button>



                {/* only shows play or pause based off flag state.. flag doesn't work must be fixed! */}
                {!flag ?  <Button variant="contained" color="primary" 
                    endIcon={<PlayCircleFilled onClick={() => handlePlay()}/>}>
                    </Button> :
                    <Button variant="contained" color="primary" 
                        endIcon={<PauseCircleFilled onClick={() => handlePause()}/>}>
                    </Button>
                }
            </div>

            </h5>

            <p>
        {quiz.map((songName, index)=> {return ( 
            (trope === index) ?

            // <img src={songName.image} onClick={() => playFromSong(index-1)} alt="icon" loading="lazy" width="50" height="50" style={ {'opacity' : '.5'} }/>
            // :<img src={songName.image} onClick={() => playFromSong(index-1)} alt="icon" loading="lazy" width="50" height="50" style={  {'border' : '0px solid black'}  }/>
            <img src={songName.image} onClick={() => playFromSong(index)} alt="icon" loading="lazy" width="50" height="50" style={ {'opacity' : '.5'} }/>
            :<img src={songName.image} onClick={() => playFromSong(index)} alt="icon" loading="lazy" width="50" height="50" style={  {'border' : '0px solid black'}  }/>

           )})}
        </p>

           </quiz>

    )
}
