import React from 'react'
import Flashcard from "./Flashcard_8";
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';

import Grid from '@material-ui/core/Grid';
import {Samples} from './Tropes3';
import Switch from '@material-ui/core/Switch';
import open from "./sounds/ui_lock.wav";
import close from "./sounds/ui_unlock.wav";



const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      color: 'green',
    },
    grid: {
      width: 500,
      height: 450,
      direction: "row",
      justify: "center",
      alignItems: "center",

    },
    paper: {
    padding: theme.spacing(1),
    border: '8px double',
    color: 'white',
    backgroundColor: 'brown',
    },
  }));



  let songList = Samples;

export default function AllTropesBox( {} ) {


  const [flip, setFlip] = React.useState(false);

    const openAudio = new Audio(open);
    const closeAudio = new Audio(close);

  const handleChange = (event) => {
    if (event.target.flip) {
        playSound(closeAudio);  
      } else {
        playSound(openAudio);
      }
    setFlip(!flip);

  };


    const classes = useStyles();

    const playSound = (song) => {
        // let audioFile =  new Audio(song);
        new Audio(song).play();
      };

    return (
<div>

         <div className={classes.root}>
            <Paper className={classes.paper} >

            <Grid container spacing={2} > 
                <Grid item xs={1}>
                    <Switch
                    checked={flip}
                    onChange={handleChange}
                    name="toggle"
                    inputProps={{ 'aria-label': 'secondary checkbox' }}
                />
                </Grid>
                <Grid item xs={10}>

            <Grid container direction='row-reverse'  > 

        {/* style= { !state.toggle ? { 'font-family': 'Times New Roman'} : { }} > */}

                {songList.map(flash => {
                            return (
                                <fl>
                            <Grid item onClick = {() => {playSound(flash.sound)}}>
                            {/* { !state.toggle ?  */}
                                {/* // {flash.heb} {'\u00A0'} : {flash.heb} {'\u00A0'}{flash.heb} {'\u00A0'} */}
                                 {flash.eng} {'\u00A0'}
                            </Grid>
                            </fl>
                            )
                    })}

                </Grid>
        </Grid>
        </Grid>


        </Paper>

        </div>
        </div>

    )
}
